import React, { useState, useEffect } from "react";
import axios from "axios";

function Atividades() {

  const [atividades, setAtividades] = useState([]);
  const [novaAtividade, setNovaAtividade] = useState({
    nome: "",
    valor: "",
    data: "",
    turmaID: "",
  });
  const [turmas, setTurmas] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/atividades")
      .then((response) => setAtividades(response.data))
      .catch((error) => console.log(error));

    axios
      .get("http://localhost:8080/turmas")
      .then((response) => setTurmas(response.data))
      .catch((error) => console.log(error));
  }, []);

  const handleChange = (e) => {
    setNovaAtividade({ ...novaAtividade, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8080/atividades", novaAtividade)
      .then((response) => {
        setAtividades([...atividades, response.data]);
        setNovaAtividade({ nome: "", valor: "", data: "", turmaID: "" });
      })
      .catch((error) => console.log(error));
  };

  return (
    <div className="container">
      <h2 className="my-4">Atividades</h2>

      <form
        onSubmit={handleSubmit}
        className="mb-4 p-4 bg-light rounded shadow-sm"
      >
        <h3 className="mb-3">Cadastrar Nova Atividade</h3>
        <div className="mb-3">
          <label htmlFor="nome" className="form-label">
            Nome da Atividade
          </label>
          <input
            type="text"
            className="form-control"
            name="nome"
            id="nome"
            placeholder="Nome da Atividade"
            value={novaAtividade.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="valor" className="form-label">
            Valor
          </label>
          <input
            type="number"
            step="0.01"
            className="form-control"
            name="valor"
            id="valor"
            placeholder="Valor da Atividade"
            value={novaAtividade.valor}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="data" className="form-label">
            Data
          </label>
          <input
            type="date"
            className="form-control"
            name="data"
            id="data"
            placeholder="Data"
            value={novaAtividade.data}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="turmaID" className="form-label">
            ID da Turma
          </label>
          <input
            type="text"
            className="form-control"
            name="turmaID"
            id="turmaID"
            placeholder="ID da Turma"
            value={novaAtividade.turmaID}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success w-100">
          Cadastrar
        </button>
      </form>

      <h3 className="mb-3">Lista de Atividades</h3>
      <div className="row">
        {atividades.map((atividade) => (
          <div key={atividade.ID} className="col-md-4">
            <div className="card mb-4 shadow-sm">
              <div className="card-body">
                <h5 className="card-title">{atividade.Nome}</h5>
                <p className="card-text">
                  Valor: {atividade.Valor} pontos - Data: {atividade.Data}
                </p>
                <p className="card-text">Turma ID: {atividade.TurmaID}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Atividades;
